import { useState, useEffect } from 'react';
import Link from 'next/link';
import { PostType } from '../types';

const PostLink: React.FunctionComponent<{ post: PostType }> = ({ post }) => (
  <li key={post.id}>
    <Link href='/p/[id]' as={`/p/${post.id}`}>
      <a>
        {post.title}
        {post.text && ` - Text Embedded`}
      </a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: 'Arial';
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </li>
);

const ArticleList: React.FunctionComponent<{ articleIDs: number[] }> = props => {
  const bufferSize = 50; // loads 50 articles at a time
  const { articleIDs } = props;
  const [currentListIdx, setCurrentListIdx] = useState<number>(0);
  //articleIDs.length >= bufferSize ? bufferSize : articleIDs.length

  const [articles, setArticles] = useState<PostType[]>([]);
  const [err, setErr] = useState<string | undefined>(undefined);

  useEffect(() => {
    // generic defines the resulting object from resolved promise
    articleIDs.splice(currentListIdx, bufferSize).map(id =>
      fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json?print=pretty`)
        .then(res => res.json())
        .then(res => setArticles(prev => [...prev, res]))
        .catch(e => setErr(e.message))
    );
  });

  if (err) {
    return <div>--- Error Occurred ---</div>;
  }

  return (
    <div>
      <ul>
        {articles.map(article => (
          <PostLink post={article} />
        ))}
      </ul>
      <button type='button' onClick={loadMore}>
        <p>Load more...</p>
      </button>
      <style jsx global>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 10px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </div>
  );
};

export default ArticleList;
