import { PostType } from '../types';

const PostLink: React.FunctionComponent<{ post: PostType }> = ({ post }) => (
  <li key={post.id}>
    <Link href='/p/[id]' as={`/p/${post.id}`}>
      <a>
        {post.title}
        {post.text && ` - Text Embedded`}
      </a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: 'Arial';
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </li>
);

const ArticleList: React.FunctionComponent = props => {
  return (
    <div>
      <ul>
        {props.articles.map(article => (
          <PostLink post={article} />
        ))}
      </ul>
      <button type='button' onClick={loadMore}>
        <p>Load more...</p>
      </button>
      <style jsx global>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 10px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </div>
  );
};

export default ArticleList;
