import Layout from '../components/MyLayout.js';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import { NextPage, NextApiRequest, NextPageContext } from 'next';
import { PostType } from '../types';

const PostLink: NextPage<{ post: PostType }> = ({ post }) => (
  <li key={post.id}>
    <Link href='/p/[id]' as={`/p/${post.id}`}>
      <a>{post.title}</a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: 'Arial';
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </li>
);

const Index: NextPage<{ articles: PostType[] }> = props => {
  return (
    <Layout>
      <h1>Hacker News</h1>
      <ul>
        {props.articles.map(article => (
          <PostLink post={article} />
        ))}
      </ul>
      <style jsx>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 10px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </Layout>
  );
};

Index.getInitialProps = async () => {
  const res = await fetch('https://hacker-news.firebaseio.com/v0/topstories.json?print=pretty');

  const data: string[] = await res.json();

  console.log(data.length);

  // generic defines the resulting object from resolved promise
  const posts: Promise<PostType>[] = [];

  data.map(id =>
    posts.push(
      fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json?print=pretty`)
        .then(res => res.json())
        .catch(e => console.log(e))
    )
  );

  const result: PostType[] = await Promise.all(posts);

  return Object.assign(
    {},
    {
      articles: result.filter(e => typeof e === 'object')
    }
  );
};
export default Index;
